'use strict';

/* Route definitions */
const routes = [
    {
        url: '/token',
        methods: ['GET'],
        handler: './handlers/token'
    }
];

/* Setting up routes */
const init = (router) => {
    routes.forEach(r => {
        r.methods.forEach(m => {
            router[m.toLowerCase()](r.url, require(r.handler)[m.toLowerCase()]);
        });
    });
    router.use(require('./handlers/404'));
};

module.exports = {init};
