'use strict';

/* Route definitions */
const routes = [
    {
        url: '/test',
        methods: ['GET'],
        handler: './handlers/test'
    }
];

/* Setting up routes */
const init = (router) => {

    /* Applying routes */
    routes.forEach(r => {
        r.methods.forEach(m => {
            router[m.toLowerCase()](r.url, require(r.handler)[m.toLowerCase()]);
        });
    });

    /* The rest goes to 404 */
    router.use(require('./handlers/404'));
};

module.exports = {init};
